DROP TABLE IF EXISTS "AuthAssignment";

CREATE TABLE `AuthAssignment` (  `itemname` varchar(64) NOT NULL,  `userid` varchar(64) NOT NULL,  `bizrule` text,  `data` text,  PRIMARY KEY (`itemname`,`userid`));




DROP TABLE IF EXISTS "AuthItem";


CREATE TABLE `AuthItem` (  `name` varchar(64) NOT NULL,  `type` int(11) NOT NULL,  `description` text,  `bizrule` text,  `data` text,  PRIMARY KEY (`name`));




DROP TABLE IF EXISTS "AuthItemChild";


CREATE TABLE `AuthItemChild` (  `parent` varchar(64) NOT NULL,  `child` varchar(64) NOT NULL,  PRIMARY KEY (`parent`,`child`));


DROP TABLE IF EXISTS "Rights";


CREATE TABLE `Rights` (  `itemname` varchar(64) NOT NULL,  `type` int(11) NOT NULL,  `weight` int(11) NOT NULL,  PRIMARY KEY (`itemname`));




DROP TABLE IF EXISTS "accCountry";


CREATE TABLE `accCountry` (  `id` varchar(2) NOT NULL DEFAULT '',  `name` varchar(255) DEFAULT NULL,  PRIMARY KEY (`id`));




DROP TABLE IF EXISTS "creditErrorCode";


CREATE TABLE `creditErrorCode` (  `ErrorID` int(3) NOT NULL DEFAULT '0',  `ErrorText` varchar(158) DEFAULT NULL,  PRIMARY KEY (`ErrorID`));




DROP TABLE IF EXISTS "currencies";


CREATE TABLE `currencies` (  `id` varchar(3) DEFAULT NULL,  `code` varchar(3) DEFAULT NULL,  `name` varchar(255) DEFAULT NULL,  `symbol` varchar(17) DEFAULT NULL);




DROP TABLE IF EXISTS "databases";



CREATE TABLE `databases` (  `id` INTEGER PRIMARY KEY AUTOINCREMENT,  `string` varchar(255) NOT NULL,  `prefix` varchar(255) NOT NULL, `user` varchar(255) NOT NULL, `password` varchar(255) NOT NULL,);

DROP TABLE IF EXISTS "databasesPerm";


CREATE TABLE `databasesPerm` (  `id` INTEGER PRIMARY KEY AUTOINCREMENT,  `user_id` int(11) NOT NULL,  `database_id` int(11) NOT NULL,  `level_id` int(11) NOT NULL);


DROP TABLE IF EXISTS "language";


CREATE TABLE `language` (  `id` varchar(10) NOT NULL,  `name` varchar(255) NOT NULL,  PRIMARY KEY (`id`));




DROP TABLE IF EXISTS "openformat";


CREATE TABLE `openformat` (  `id` int(11) NOT NULL,  `description` text NOT NULL,  `type` text NOT NULL,  `size` int(11) NOT NULL,  `record` int(11) NOT NULL,  `export` text NOT NULL,  `import` text NOT NULL,  `type_id` varchar(4) NOT NULL,  PRIMARY KEY (`id`));




DROP TABLE IF EXISTS "openformattype";


CREATE TABLE `openformattype` (  `id` varchar(4) NOT NULL,  `description` varchar(26) DEFAULT NULL,  `type` varchar(8) NOT NULL DEFAULT '',  PRIMARY KEY (`id`,`type`));





DROP TABLE IF EXISTS "user";

CREATE TABLE `user` (  `id` INTEGER PRIMARY KEY AUTOINCREMENT,  `username` varchar(100) NOT NULL,  `fname` varchar(255) DEFAULT NULL,  `lname` varchar(255) NOT NULL,  `password` char(41) DEFAULT NULL,  `lastlogin` datetime DEFAULT NULL,  `cookie` char(32) DEFAULT NULL,  `hash` char(32) DEFAULT NULL,  `certpasswd` varchar(255) NOT NULL,  `salt` varchar(255) NOT NULL,  `email` varchar(255) NOT NULL,  `language` varchar(10) NOT NULL,  `theme` varchar(255) NOT NULL,  `timezone` varchar(255) NOT NULL);

CREATE TABLE `menu` (  `id` INTEGER PRIMARY KEY AUTOINCREMENT,  `label` varchar(255) DEFAULT NULL,  `url` varchar(255) DEFAULT NULL,  `icon` varchar(255) DEFAULT NULL,  `parent` int(12) NOT NULL DEFAULT '0');

